      }

function() {
      this.test.cell = {columnId, rowId: this.test.title};
      const multibase = 'z';
      proofs.some(proof => {
        const value = proof?.proofValue;
        return value.startsWith(multibase) && shouldBeBs58(value);
      }).should.equal(
        true,
        'Expected "proof.proofValue" to be multibase-encoded ' +
          'base58-btc value.'
      );
    }

